import java.util.*;
import java.math.*;
import java.lang.Object;

public class Euler008
{
	public static void main( String[] args )
	{
		String num1000 = "";
		
		int[] arr = new int[1000];
		long max = 1L, tempMax = 1L;
		
		for(int i = 0; i < arr.length; i++){
			arr[i] = (int) (num1000.charAt(i) - '0');
		}
		for(int c = 12; c < arr.length; c++){
			tempMax = 1L;
			for(int x = 0; x < 13; x++){
				tempMax = tempMax * arr[c - x];
			}
			if( tempMax >= max)
				max = tempMax;
		}
		System.out.println(max);
	}
}